// SPDX-License-Identifier: MIT
pragma solidity ^0.5.0;

import "../../node_modules/openzeppelin-solidity/contracts/math/SafeMath.sol";


contract Token {

  using SafeMath for uint;
  // Declare variable value type. Public creates fx that gives access to read "name", after deployment.

  // Token Name, Symbol, Decimals, and Total Supply

  string public name = "Lumine";
  string public symbol = "LUMI";
  uint256 public decimals = 18;
  uint256 public totalSupply;
  /* Track Balances */
  mapping(address => uint256) public balanceOf;
  /* Send Tokens */
  function transfer(address _to, uint256 _value) public returns (bool success) {
    balanceOf[msg.sender] = balanceOf[msg.sender].sub(_value);
    balanceOf[_to] = balanceOf[_to].add(_value);
    return true;
  }

  constructor() public {
    // Create 270 million tokens
    totalSupply = 270000000 * (10 ** decimals);
    //key value mapping 
    balanceOf[msg.sender] = totalSupply;
  }
}
